class Handles # Création d'une classe Handles qui contiendra mon tableau d'handles et toutes les méthodes qui peuvent lui être appliquées
  attr_accessor :array
  def initialize (array)
    @array = array
  end

  def do(action)
    send(action) # Méthode qui me permets de lancer une autre méthode de la même classe
  end

  def length
    "Le tableau contient #{"\e[36m#{@array.length}\e[0m"} handles (cé bokou)" # Retourne la taille du tableau
  end

  def min
    result = @array.min {|a, b| a.length <=> b.length} # Cherche le handle le plus court
    "Le handle le plus court du tableau est #{"\e[36m#{result}\e[0m"}" # Retourne le résultat
  end

  def five
    result = @array.count{|handle| handle.length == 6} # Compte les handles qui contiennent 5 caractères (+ le @)
    "Il y a #{"\e[36m#{result}\e[0m"} handles de 5 caractères (cette info ne sert à rien)" # Retourne le résultat
  end

  def upcase
    result = @array.count{|handle| handle =~ /\A@[A-Z]/} # Compte le nombre d'handles qui commencent par une majuscule avec l'aide d'une RegExp
    "Il y a #{"\e[36m#{result}\e[0m"} handles commençant par une majuscule (cette info ne sert à rien non plus" # Retourne le résultat
  end

  def alpha
    @array.sort{|a, b| a.downcase <=> b.downcase} # Trie le tableau par ordre alphabétique. Tous les handles sont mis en minuscule dans la méthode de tri pour éviter d'avoir d'abord les majuscules triées, puis les minuscules
  end

  def small_to_big
    @array.sort{|a, b| a.length <=> b.length} # Trie le tableau selon la taille des éléments
  end

  def epenser
    result = @array.find_index("@epenser") # Trouve l'index d'@epenser
    "@epenser se trouve à l'index #{"\e[36m#{result}\e[0m"} \e[92m(Prenez le temps d'e-penser)\e[0m" # Retourne le résultat
  end

  def breakdown
    @array.group_by(&:length).map{|k, v| [k - 1, v]}.sort # Groupe les handles par taille, enlève 1 à cette taille pour omettre le @, puis les trie du plus petit au plus grand
  end
end

